using Microsoft.UI.Xaml; using Windows.Services.Store; using WinRT.Interop;  namespace PlaygamaBridgeMicrosoftStore {     public sealed partial class MainWindow : Window     {         private readonly StoreContext _store;         private readonly nint _hwnd;          public MainWindow()         {             InitializeComponent();              _hwnd = WindowNative.GetWindowHandle(this);              SetWindowIcon("Assets\\favicon.ico");              ExtendsContentIntoTitleBar = true;             SetTitleBar(AppTitleBar);              _store = StoreContext.GetDefault();             InitializeWithWindow.Initialize(_store, _hwnd);              _ = InitializeAsync();         }     } } 